x1 = [1 1.05 1.07 1.10];f = @(x)3*x.*exp(x)-exp(2*x);d = @(x)3*exp(x) + 3*x.*exp(x) - 2*exp(2*x);interval = 1:0.001:5;n = 1.03;h1 = HermitePol(x1, f(x1), d(x1), n)H1 = HermitePol(x1, f(x1), d(x1), interval);e1 = abs(f(n)-h1)x2 = [1 1.05 1.07 1.11 1.13];h2 = HermitePol(x2, f(x2), d(x2), n)H2 = HermitePol(x2, f(x2), d(x2), interval);e2 = abs(f(n)-h2)hold on; plot(interval, f(interval));plot(interval, H1);plot(interval, H2);